    DECLARE item_id_declared INT;
    DECLARE purchase_quantity INT;
    DECLARE current_quantity INT;
DECLARE store_id_declared INT;
    SET item_id_declared = NEW.item_id;
    SET purchase_quantity = NEW.quantity;

    
    SELECT store_id INTO store_id_declared FROM purchase WHERE purchase_id = NEW.purchase_id;
    
    SELECT quantity INTO current_quantity FROM store_items WHERE item_id = item_id_declared AND store_id = store_id_declared LIMIT 1;

    IF purchase_quantity <= current_quantity THEN
        UPDATE store_items SET quantity = current_quantity - purchase_quantity WHERE item_id = item_id_declared AND store_id = store_id_declared; 
    ELSE
        DELETE FROM purchase WHERE purchase_id = NEW.purchase_id;
    END IF;
